const compute=document.getElementById("compute"),update=e=>{const t=document.getElementById("answer");t.innerHTML=e};compute.addEventListener("click",e=>{e.preventDefault();var t=document.getElementById("var_x").value,n=document.getElementById("selectedOperation").value,e=document.getElementById("var_y").value;update(Calculator(n,t,e))});const operations=(e,t)=>({add:()=>e+t,subtract:()=>e-t,multiply:()=>e*t,divide:()=>e/t}),Calculator=(e,t,n)=>operations(Number(t),Number(n))[e]();